import { Container } from "./style"
import { useHistory } from "react-router"
import { useContext } from "react";
import { CasamentoCartContext } from "../../provider/CasamentoCart";
import { FormaturaCartContext } from "../../provider/FormaturaCart";
import { ConfraternizacaoCartContext } from "../../provider/ConfraternizacaoCart";



export const Header = () => {


    const { confraternizacaoCart } = useContext(ConfraternizacaoCartContext)
    const { casamentoCart } = useContext(ConfraternizacaoCartContext)
    const { formaturaCart } = useContext(ConfraternizacaoCartContext)

    console.log(formaturaCart)
    //const totalFormaturaCart = formaturaCart.reduce((a, b) => { return (a + b.numberUnits) }, 0);






    const history = useHistory()


    return (

        <Container>
            <div className="logo" onClick={() => { history.push("/") }}>Bebidando</div>
            <ul>
                <li onClick={() => { history.push("/casamento") }}>Casamento</li>
                <li onClick={() => { history.push("/formatura") }}>Formatura</li>
                <li onClick={() => { history.push("/confraternizacao") }}>Confraternização</li>
            </ul>

        </Container>
    )
}
