import { Container } from "./style"
import { Header } from "../../components/header"
import api from "../../services/api"
import { useContext, useEffect, useState } from "react"
import { ItemCard } from "../../components/CardItens/cardItens"
import { Modal } from "../../components/modal"
import { ProductsContext } from "../../provider/products"
import { CartContext } from "../../provider/cart"
import { AiOutlineArrowRight, AiOutlineArrowLeft } from "react-icons/ai";


const Home = () => {

    const { CapturePerPage } = useContext(ProductsContext)
    const products = CapturePerPage(1)
    const { cart, addCart } = useContext(CartContext)
    const [page, setPage] = useState(1)
    const [openModal, setOpenModal] = useState(false)





    return (
        <>
            <Header></Header>
            <Container>


                <div className="menu"></div>
                <div className="pageLeft"></div>
                <div className="products">
                    {products.map((item) => {
                        return (

                            <ItemCard item={item} key={item.id}></ItemCard>
                        )
                    })}

                </div>
                <div className="pageRight"></div>



            </Container>
            {openModal && <Modal close={setOpenModal} />}
        </>
    )
}

export default Home
