import styled from "styled-components";

export const Container = styled.div`
  height: 200px;
  width: 600px;
  background-color: #ffffff;
  display: flex;
  align-items: center;
  margin: 0 0 15px 0;
  border-radius: 5px;
  justify-content: space-between;

  .imgContainer img {
    height: 100%;
    width: auto;
  }
  h3 {
    margin: 10px 0 3px 0;
    font-size: 12px;
  }
  .textImg {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    width: 120px;
  }

  .imgContainer {
    height: 150px;
    width: 80px;
    margin: 15px 0 0 0;
  }

  .titleCenter {
    display: flex;
    flex-direction: column;
  }

  .date {
    font-size: 14px;
  }

  .description {
    font-size: 14px;
    text-align: justify;
    width: 350px;
    overflow-y: auto;
    height: 100px;
  }
  .Volume {
    font-weight: bold;
    margin: 15px 0 0 0;
  }
`;
