import styled from "styled-components";

export const Container = styled.div`
  height: 400px;
  width: 250px;
  background-color: #ffffff;
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 10px;
  border-radius: 5px;

  .imgContainer img {
    height: 100%;
    width: auto;
  }
  h3 {
    margin: 10px 0 3px 0;
  }

  .imgContainer {
    height: 160px;
    width: 80px;
    margin: 15px 0 0 0;
  }

  .date {
    font-size: 12px;
  }

  .description {
    font-size: 14px;
    text-align: justify;
    width: 80%;
    overflow-y: auto;
    height: 60px;
  }
  .Volume {
    font-weight: bold;
    margin: 15px 0 0 0;
  }
`;
