import styled from "styled-components";

export const Container = styled.div`
  height: 300px;
  width: 200px;
  background-color: #ffffff;
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 10px;
  border-radius: 5px;

  .imgContainer img {
    height: 100%;
    width: auto;
  }

  .imgContainer {
    height: 160px;
    width: 80px;
  }

  .date {
    font-size: 12px;
  }
`;
