import { Container } from "./style"
import { Header } from "../../components/header"
import api from "../../services/api"
import { useContext, useEffect, useState } from "react"
import { ItemCard } from "../../components/CardItens/cardItens"
import { Modal } from "../../components/modal"
import { ProductsContext } from "../../provider/products"


const Home = () => {

    const [itens, setItens] = useState([]);
    const { itenss } = useContext(ProductsContext)
    const [page, setPage] = useState(1)
    const [openModal, setOpenModal] = useState(false)

    useEffect(() => {
        api.get(`/beers?page=2&per_page=10`)

            .then((response) => setItens(response.data))
            .catch((err) => console.log(err));
    }, [])

    console.log(itenss)



    return (
        <>
            <Header></Header>
            <Container>


                <div className="menu"></div>
                <div className="pageLeft"></div>
                <div className="products">
                    {itens.map((item) => {
                        return (

                            <ItemCard item={item}></ItemCard>
                        )
                    })}

                </div>
                <div className="pageRight"></div>



            </Container>
            {openModal && <Modal close={setOpenModal} />}
        </>
    )
}

export default Home
