import styled from "styled-components";

export const Container = styled.div`
  height: 89vh;
  width: 100vw;
  display: flex;
  background-color: #f5f5f5;

  .menu {
    height: 100%;
    width: 15%;
    background-color: #660708;
  }
  .products {
    display: flex;
    flex-direction: column;
    flex-wrap: nowrap;
    width: 620px;
    height: 70%;
    margin: 10px 0 0 10px;
    overflow-x: auto;
  }
  h2 {
    color: #660708;
  }
`;
