import styled from "styled-components";

export const Container = styled.div`
  height: 89vh;
  width: 100vw;
  display: flex;
  background-color: #f5f5f5;

  .menu {
    height: 100%;
    width: 15%;
    background-color: #6a040f;
    border-top: 1px solid #ffffff;
  }
  .products {
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    width: 80%;
    margin: 10px 0 0 10px;
  }
  .pageLeft {
    width: 50px;
    margin: auto auto;
  }
  .pageLeft button {
    border-radius: 100%;
    border: none;
    height: 30px;
    width: 30px;
    background-color: #6a040f;
    display: flex;
    align-items: center;
    margin: auto;
  }

  .pageLeft button svg {
    height: 20px;
    width: 20px;
    color: #ffffff;
  }
`;
