import styled from "styled-components";

export const Container = styled.div`
  display: flex;
  height: 20%;
  min-height: 100px;
  background-color: #6a040f;
  justify-content: space-between;
  align-items: center;
  color: #ffffff;

  ul {
    display: flex;
    margin: 0 30px 0 0;
  }

  ul li {
    list-style: none;
    margin: 0 10px 0 10px;
    font-size: 20px;
    height: 100%;
  }
  ul li:hover {
    cursor: pointer;
    text-decoration: underline;
  }
  .logo {
    margin: 0 0 0 50px;
    font-size: 26px;
    font-weight: bold;
  }
  .logo:hover {
    cursor: pointer;
  }
`;
